var vars = [], hash;
function getUrlVars()
{
    var hashes = window.location.href.slice(window.location.href.indexOf('?') + 1).split('&');
    for(var i = 0; i < hashes.length; i++)
    {
        hash = hashes[i].split('=');
        let aa="";
        for(let k=1;k<hash.length;k++){
        aa+="="+hash[k];
        }
        vars.push(hash[0]);
        vars[hash[0]] = aa.substring(1);
    }
    return vars;
}

