function notificationSocket(){
    Notification.requestPermission().then(function (permission) {
        console.log(permission);
    // Create WebSocket connection.
    socket = new WebSocket(socketUrl);
    //socket = new WebSocket('wss://demo.piesocket.com/v3/channel_1?api_key=VCXCEuvhGcBDP7XhiJJUDvR1e1D3eiVjgZ9VRiaV');

    // Connection opened
    socket.addEventListener('open', function (event) {
        //socket.send('Hello Server!');
    });

    // Listen for messages
    socket.addEventListener('message', function (event) {
        console.log('Message from server ', event.data);
        // var data=event.data.json();
        // console.log(data);
        let permission = Notification.permission;
        if(permission != "denied") {
            var title = "Breaking News";
            icon = 'data:image/png;base64,';
            var body = "It's Your boarding time ";
            let data=JSON.parse(event.data);
            // title=data["title"];
            body=data["title"];


            if(counter%2==0){
            var notification = new Notification(title, { body, icon });
            notification.onclick = () => {
                window.open("./single.html?pp="+data["url"],"_blank");
                var msg = new SpeechSynthesisUtterance(''+body);
                window.speechSynthesis.speak(msg);
            }
         }
         counter++;
        }
    });
    });

}
